def ImageCreate2(ccd_data1, name1, ccd_data2, name2, lastMiddlePosition1=64, lastMiddlePosition2=64, midline1=None, midline2=None):
    left_edge1, right_edge1, mid_line1 = find_road_edges(ccd_data1, lastMiddlePosition1)
    left_edge2, right_edge2, mid_line2 = find_road_edges(ccd_data2, lastMiddlePosition2)

    fig, (ax1, ax2) = plt.subplots(1, 2, figsize=(8, 4))

    # Plot for ccd_data1
    ax1.plot(ccd_data1, label=name1, color='red')
    ax1.axvline(x=mid_line1, color='blue', linestyle='-', label='Middle Line')
    ax1.axvline(x=64, color='black', linestyle='--', label='x=64')
    if midline1:
        ax1.axvline(x=midline1, color='#006400', linestyle='--', label='CircleFilledMidline')
    ax1.set_title(name1)
    ax1.set_xlabel(f'left, mid_line, right: {left_edge1, mid_line1, right_edge1}')
    ax1.set_ylabel('Value')
    ax1.legend()

    # Plot for ccd_data2
    ax2.plot(ccd_data2, label=name2, color='green')
    ax2.axvline(x=mid_line2, color='purple', linestyle='-', label='Middle Line')
    ax2.axvline(x=64, color='black', linestyle='--', label='x=64')
    if midline2:
        ax2.axvline(x=midline2, color='#FFD700', linestyle='--', label='CircleFilledMidline')
    ax2.set_title(name2)
    ax2.set_xlabel(f'left, mid_line, right: {left_edge2, mid_line2, right_edge2}')
    ax2.set_ylabel('Value')
    ax2.legend()

    plt.tight_layout()
    plt.show()

def ImageCreate(ccd_data, name, lastMiddlePosition=64,midline = None):
    left_edge, right_edge, mid_line = find_road_edges(ccd_data,lastMiddlePosition)
    plt.figure(figsize=(8, 6))
    plt.plot(ccd_data, label=name, color='red')
    plt.axvline(x=mid_line, color='blue', linestyle='-', label='Middle Line')
    plt.axvline(x=64, color='black', linestyle='--', label='x=64')
    if midline:
        plt.axvline(x=midline, color='#006400', linestyle='--', label='CircleFilledMidline')

    plt.title(name)
    plt.xlabel(f'left, mid_line, right: {left_edge, mid_line, right_edge}')
    plt.ylabel('Value')
    plt.legend()
    plt.show()


for i in range(0, len(cross1)):
    ImageCreate2(cross1[i], "ccd1", cross2[i], "ccd2")
